// Get the URL in the address bar
var addressBarUrl = window.location.href;

// Extract the date and fileName parameters from the URL
var urlParams = new URLSearchParams(addressBarUrl.substring(addressBarUrl.indexOf('?') + 1));
var todaysDate = urlParams.get('date');
var fileName = urlParams.get('fileName');

// Construct the imgUrl variable with the extracted parameters
var imgUrl = "https://svgtrace-upload-results-bucket.s3.amazonaws.com/" + todaysDate + "/" + fileName + ".svg";

// remove the hashtags from imgUrl
imgUrl = imgUrl.replace(/#/g, '');


// Update the src attribute of the image with the constructed imgUrl variable
document.getElementById("imagePreview").src = imgUrl;

