    // Get the URL in the address bar
    var addressBarUrl = window.location.href;

    // Extract the date and fileName parameters from the URL
    var urlParams = new URLSearchParams(addressBarUrl.substring(addressBarUrl.indexOf('?') + 1));
    var todaysDate = urlParams.get('date');
    var fileName = urlParams.get('fileName');


    // Construct the downloadUrl variable with the extracted parameters
    var downloadUrl = "https://svgtrace-upload-results-bucket.s3.amazonaws.com/" + todaysDate + "/" + fileName + ".svg";
    console.log("downloadUrl: " + downloadUrl)
    // delete all hashtags from the url
    downloadUrl = downloadUrl.replace(/#/g, '');


    console.log("downloadUrl: " + downloadUrl)
    // Update the href attribute of the download link with the constructed downloadUrl variable
    // document.getElementById("downloadLink").href = downloadUrl;

    // Add click event listener to the download link
    document.getElementById("downloadLink").addEventListener("click", function() {
        fetch(downloadUrl)
            .then(response => response.blob())
            .then(blob => {
                const url = window.URL.createObjectURL(new Blob([blob]));
                const link = document.createElement('a');
                link.href = url;
                console.log("UPM: " + url)
                link.setAttribute('download', fileName + ".svg");
                document.body.appendChild(link);
                link.click();
                link.parentNode.removeChild(link);
            });
    });
